/**
 * Use Size
 *
 * React hook that returns the current size of the client width in terms:
 * xs (extra small)
 * sm (small)
 * md (medium)
 * lg (large)
 * xl (extra large)
 *
 * @name useSize
 * @access public
 * @returns 'xs' | 'sm' | 'md' | 'lg' | 'xl'
 */
export default function useSize(): string;
